import React, { useState } from 'react'
import { useAuth } from '../context/AuthContext.js'

function BossDashboard() {
  const { user, logout } = useAuth();
  const [filters, setFilters] = useState({
    fechaInicio: '',
    fechaFin: '',
    departamento: '',
    persona: '',
    lider: ''
  });
  const [reports, setReports] = useState([]);

  const departments = ['medicare', 'vida', 'auto', 'casa', 'comercial'];
  const leaders = ['Alfonso', 'María', 'Carlos', 'Ana', 'Pedro'];

  const handleFilterChange = (field, value) => {
    setFilters(prev => ({ ...prev, [field]: value }));
  };

  const searchReports = () => {
    // Simulando búsqueda de reportes
    const mockReports = [
      {
        id: 1,
        fecha: '2024-01-15',
        departamento: 'medicare',
        lider: 'Alfonso',
        empleado: 'Juan Pérez',
        totalVentas: 5000,
        descripcion: 'Venta de pólizas medicare',
        comentarios: 'Excelente desempeño'
      },
      {
        id: 2,
        fecha: '2024-01-15',
        departamento: 'vida',
        lider: 'María',
        empleado: 'Ana Martín',
        totalVentas: 3500,
        descripcion: 'Seguros de vida familiar',
        comentarios: 'Buen trabajo'
      }
    ];

    setReports(mockReports);
  };

  const clearFilters = () => {
    setFilters({
      fechaInicio: '',
      fechaFin: '',
      departamento: '',
      persona: '',
      lider: ''
    });
    setReports([]);
  };

  return (
    <div style={{ padding: '20px', maxWidth: '1400px', margin: '0 auto' }}>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
        <h1>Panel Ejecutivo - {user.name}</h1>
        <button onClick={logout} style={{ padding: '8px 16px' }}>Cerrar Sesión</button>
      </div>

      {/* Filtros */}
      <div style={{ 
        backgroundColor: '#f8f9fa', 
        padding: '20px', 
        borderRadius: '8px', 
        marginBottom: '30px' 
      }}>
        <h3>Filtros de Búsqueda</h3>
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
          gap: '15px',
          marginTop: '15px'
        }}>
          <div>
            <label>Fecha Inicio:</label>
            <input
              type="date"
              value={filters.fechaInicio}
              onChange={(e) => handleFilterChange('fechaInicio', e.target.value)}
              style={{ width: '100%', padding: '8px', marginTop: '5px' }}
            />
          </div>
          <div>
            <label>Fecha Fin:</label>
            <input
              type="date"
              value={filters.fechaFin}
              onChange={(e) => handleFilterChange('fechaFin', e.target.value)}
              style={{ width: '100%', padding: '8px', marginTop: '5px' }}
            />
          </div>
          <div>
            <label>Departamento:</label>
            <select
              value={filters.departamento}
              onChange={(e) => handleFilterChange('departamento', e.target.value)}
              style={{ width: '100%', padding: '8px', marginTop: '5px' }}
            >
              <option value="">Todos</option>
              {departments.map(dept => (
                <option key={dept} value={dept}>{dept.toUpperCase()}</option>
              ))}
            </select>
          </div>
          <div>
            <label>Líder:</label>
            <select
              value={filters.lider}
              onChange={(e) => handleFilterChange('lider', e.target.value)}
              style={{ width: '100%', padding: '8px', marginTop: '5px' }}
            >
              <option value="">Todos</option>
              {leaders.map(leader => (
                <option key={leader} value={leader}>{leader}</option>
              ))}
            </select>
          </div>
          <div>
            <label>Empleado:</label>
            <input
              type="text"
              placeholder="Nombre del empleado"
              value={filters.persona}
              onChange={(e) => handleFilterChange('persona', e.target.value)}
              style={{ width: '100%', padding: '8px', marginTop: '5px' }}
            />
          </div>
        </div>
        <div style={{ marginTop: '20px' }}>
          <button 
            onClick={searchReports}
            style={{ 
              padding: '10px 20px', 
              backgroundColor: '#007bff', 
              color: 'white', 
              border: 'none',
              borderRadius: '4px',
              marginRight: '10px'
            }}
          >
            Buscar Reportes
          </button>
          <button 
            onClick={clearFilters}
            style={{ 
              padding: '10px 20px', 
              backgroundColor: '#6c757d', 
              color: 'white', 
              border: 'none',
              borderRadius: '4px'
            }}
          >
            Limpiar Filtros
          </button>
        </div>
      </div>

      {/* Resultados */}
      {reports.length > 0 && (
        <div>
          <h3>Resultados ({reports.length} reportes encontrados)</h3>
          <div style={{ overflowX: 'auto' }}>
            <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '15px' }}>
              <thead>
                <tr style={{ backgroundColor: '#e9ecef' }}>
                  <th style={{ padding: '12px', border: '1px solid #ddd' }}>Fecha</th>
                  <th style={{ padding: '12px', border: '1px solid #ddd' }}>Departamento</th>
                  <th style={{ padding: '12px', border: '1px solid #ddd' }}>Líder</th>
                  <th style={{ padding: '12px', border: '1px solid #ddd' }}>Empleado</th>
                  <th style={{ padding: '12px', border: '1px solid #ddd' }}>Ventas ($)</th>
                  <th style={{ padding: '12px', border: '1px solid #ddd' }}>Descripción</th>
                  <th style={{ padding: '12px', border: '1px solid #ddd' }}>Comentarios</th>
                </tr>
              </thead>
              <tbody>
                {reports.map(report => (
                  <tr key={report.id}>
                    <td style={{ padding: '12px', border: '1px solid #ddd' }}>{report.fecha}</td>
                    <td style={{ padding: '12px', border: '1px solid #ddd' }}>{report.departamento.toUpperCase()}</td>
                    <td style={{ padding: '12px', border: '1px solid #ddd' }}>{report.lider}</td>
                    <td style={{ padding: '12px', border: '1px solid #ddd' }}>{report.empleado}</td>
                    <td style={{ padding: '12px', border: '1px solid #ddd' }}>${report.totalVentas.toLocaleString()}</td>
                    <td style={{ padding: '12px', border: '1px solid #ddd' }}>{report.descripcion}</td>
                    <td style={{ padding: '12px', border: '1px solid #ddd' }}>{report.comentarios}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
}

export default BossDashboard;
